import React from 'react';
import { Radio } from 'antd';
import 'antd/lib/radio/style/css'


const RadioButton = Radio.Button;
const RadioGroup = Radio.Group;

const Menu = ({
  views,
  onChangeView,
}) => {
  const handleChange = (e) => {
    const nextView = e.target.value;
    onChangeView(nextView);
  };

  return (
    <div className="cal-heatmap_menu">
      <RadioGroup onChange={handleChange} defaultValue={views[0].dataIndex}>
        {views.map(view => {
          const { nameDisplay, dataIndex } = view;
          return <RadioButton key={dataIndex} value={dataIndex}>{nameDisplay}</RadioButton>;
        })}
      </RadioGroup>
    </div>
  );
};

export default Menu;
